	} while ((c == 'c') || (c == 'C'));
}

int doixung(char s[])
{
	int i, n, d;
	n = strlen(s);
	d = 1;
	if (n > 0)
		for (i = 0;
			(i <= n / 2) && d; i++)
			d = (s[i] == s[n - 1 - i]);
	return d;
}

