void nhap(char k, int p, int q)
{
	int i, j, t;
	for (i = 0; i < p; i++)
	{
		cout << "Nhap cac phan tu hang thu " << i + 1 << ":\n";
		for (j = 0; j < q; j++)
		{
			cout << k << "[" << i + 1 << "," << j + 1 << "]=";
			cin >> t;
			switch (k)
			{
				case 'a':
					a[i][j] = t;
					break;
				case 'b':
					b[i][j] = t;
			}
		}
	}
}

void xuat(char k, int p, int q)
{
	int i, j;
	for (i = 0; i < p; i++)
	{
		cout << endl;
		for (j = 0; j < q; j++)
		{
			cout << " ";
			switch (k)
			{
				case 'a':
					cout << a[i][j];
					break;
				case 'b':
					cout << b[i][j];
					break;
				case 'c':
					cout << c[i][j];
			}
		}
	}
}

void nhan(int m, int n, int p)
{
	int i, j, k, s;
	for (i = 0; i < m; i++)
		for (j = 0; j < p; j++)
		{
			for (k = s = 0; k < n; k++) s += a[i][k] *b[k][j];
			c[i][j] = s;
		}
}

